import React, { useState, useEffect } from 'react';
import api from './services/api';

import './global.css';
import './App.css';
import './Sidebar.css';
import './Main.css';

//COMPONENTE :  Bloco isolado de HTML, CSS e JS, o qual não interfere no restante da aplicação
//PROPRIEDADE: Informações que um componente PAI para o componente FILHO
//ESTADO: informações mantidas pelo componente (lembrar: imutabilidade)


function App() {

  const [devs, setDevs]  = useState([]);

  const [github_username, setGithub_Username] = useState('');
  const [techs, setTechs] = useState('');

  const [latitude, setLatitude] = useState('');
  const [longitude, setLongitude] = useState('');

  useEffect(()=>{
    navigator.geolocation.getCurrentPosition(
      (position)=>{
        const {latitude, longitude} = position.coords;

        setLatitude(latitude);
        setLongitude(longitude);

      },
      (err)=>{
        console.log(err);
      },
      {
        timeout: 3000,
      }
    )
  },[]);

  useEffect(()=>{
    async function loadDevs(){
      const response = await api.get('/devs');

      setDevs(response.data);
    }
      loadDevs();

  },[]);

  async function handleAddDev(e){
    e.preventDefault();

    const response = await api.post('/devs',{
      github_username,
      techs,
      latitude,
      longitude,
    })

    setGithub_Username('');
    setTechs('');
    setDevs([...devs, response.data]);


  }


  return (
    <div id="app">
      <aside>
        <strong>Cadastrar</strong>
        <form onSubmit={handleAddDev}>
          <div className="input-block">
          <label htmlFor="github_username">Usuário do Github</label>
          <input name="github_username" id="github_username" required value={github_username}
              onChange={e => setGithub_Username(e.target.value)}
          ></input>
          </div>

          <div className="input-block">
          <label htmlFor="techs">Tecnologias</label>
          <input name="techs" id="techs" required value={techs}
              onChange={e => setTechs(e.target.value)}
          ></input>
          </div>


          <div className="input-group">

              <div className="input-block">
              <label htmlFor="latitude">Latitude</label>
              <input type="number" name="latitude" id="latitude" required value={latitude}
              onChange={e => setLatitude(e.target.value)}
              ></input>
              </div>


              <div className="input-block">
              <label htmlFor="longitude">Longitude</label>
              <input type="number" name="Longitude" id="Longitude" required value={longitude}
               onChange={e => setLongitude(e.target.value)}
              ></input>
              </div>
            </div>

            <button type="submit">Salvar</button>

          </form>
      </aside>

      <main>
        <ul>
          {devs.map(dev =>(
            <li key={dev.id} className="dev-item">
            <header>
              <img src={dev.avatar_url} alt={dev.name}></img>
              <div className="user-info">
                <strong>{dev.name}</strong>
                <span>{dev.techs.join(', ')}</span>
              </div> 
            </header>
            <p>{dev.bio}</p>
            <a href={`https://github.com/liveicaro/${dev.github_username}`}>Acessar perfil no Github</a>
          </li>
          ))}




        </ul>


      </main>

    </div>
  );
}

export default App;
